// src/pages/CartPage.jsx
import React, { useEffect } from "react";
import { useCartContext } from "../context/CartContext";
import { fetchCart } from "../api/cartService";
import { useAuthContext } from "../hooks/useAuthContext";

function CartPage() {
    const { state, dispatch } = useCartContext();
    const { cart } = state;
    const { user } = useAuthContext();

    useEffect(() => {
        const loadCart = async () => {
            if (user) {
                console.log("User Token:", user.token); // Debugging line
                try {
                    const cartData = await fetchCart(user.token);
                    dispatch({ type: "SET_CART", payload: cartData.cartItems });
                } catch (error) {
                    console.error("Error fetching cart:", error); // Debugging line
                    alert("Failed to fetch cart."); // Using standard alert
                }
            }
        };
        loadCart();
    }, [user, dispatch]);

    const updateQuantity = (item, quantity) => {
        const updatedCart = cart.map((cartItem) => {
            if (cartItem.product === item.product) {
                return { ...cartItem, quantity };
            }
            return cartItem;
        });
        dispatch({ type: "SET_CART", payload: updatedCart });
    };

    const handleRemove = (product) => {
        const updatedCart = cart.filter((cartItem) => cartItem.product !== product);
        dispatch({ type: "SET_CART", payload: updatedCart });
    };

    return (
        <div>
            <h1>My Cart</h1>
            {cart.length === 0 ? (
                <p>Your cart is empty.</p>
            ) : (
                cart.map((item) => (
                    <div key={item.product}>
                        <p>Product ID: {item.product}</p>
                        <p>
                            Quantity:
                            <select
                                value={item.quantity}
                                onChange={(e) => updateQuantity(item, e.target.value)}
                            >
                                {[...Array(9)].map((_, index) => (
                                    <option key={index} value={index + 1}>
                                        {index + 1}
                                    </option>
                                ))}
                            </select>
                        </p>
                        <button onClick={() => handleRemove(item.product)}>Remove</button>
                    </div>
                ))
            )}
        </div>
    );
}

export default CartPage;
