// src/pages/ProductPage.jsx
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useCartContext } from "../context/CartContext";
import { fetchProduct } from "../api/productService";
import { addItemToCart } from "../api/cartService";

function ProductPage() {
  const { id } = useParams();
  const { state, dispatch } = useCartContext();
  const { cart } = state; // Access cart from context
  const [productDetails, setProductDetails] = useState(null);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    const loadProduct = async () => {
      const product = await fetchProduct(id);
      setProductDetails(product);
    };

    loadProduct();
  }, [id]);

  const handleAddToCart = () => {
    const cartItem = { product: productDetails.id, quantity };
    dispatch({ type: "ADD_ITEM", payload: cartItem });
    alert("Item added to cart!"); // Simple alert for success
  };

  if (!productDetails) return <div>Loading...</div>;

  return (
    <div>
      <h1>{productDetails.name}</h1>
      <p>Price: ${productDetails.price}</p>
      <select value={quantity} onChange={(e) => setQuantity(Number(e.target.value))}>
        {[...Array(9)].map((_, index) => (
          <option key={index} value={index + 1}>{index + 1}</option>
        ))}
      </select>
      <button onClick={handleAddToCart}>Add to Cart</button>
    </div>
  );
}

export default ProductPage;
